import React, { useState } from "react";
import "materialize-css/dist/css/materialize.min.css"; // Importe o arquivo CSS do Materialize

function App() {
  const [selectedComponent, setSelectedComponent] = useState(""); // Estado para controlar o componente selecionado

  return (
    <div className="container">
      <h5 className="header center-align">Simulação do Bulbo Úmido</h5>
      <div className="input-field col s12">
        <select
          className="browser-default"
          value={selectedComponent}
          onChange={(e) => setSelectedComponent(e.target.value)}
        >
          <option value="" disabled>
            Escolha Uma Região
          </option>
          <option value="Norte">Norte</option>
          <option value="Nordeste">Nordeste</option>
          <option value="CentroOeste">Centro Oeste</option>
          <option value="Sudeste">Sudeste</option>
          <option value="Sul">Sul</option>
        </select>
      </div>
      <div className="row">
        <div className="col s12">
          {selectedComponent === "Norte" && (
            <>
              <h3>Propriedades do Solo</h3>
              {/* Renderize os dados específicos para Propriedades do Solo */}
            </>
          )}
          {selectedComponent === "Nordeste" && (
            <>
              <h3>Condições Iniciais</h3>
              {/* Renderize os dados específicos para Condições Iniciais */}
            </>
          )}
          {selectedComponent === "CentroOeste" && (
            <>
              <h3>Condições de Contorno</h3>
              {/* Renderize os dados específicos para Condições de Contorno */}
            </>
          )}
          {selectedComponent === "Sudeste" && (
            <>
              <h3>Parâmetros Climáticos</h3>
              {/* Renderize os dados específicos para Parâmetros Climáticos */}
            </>
          )}
          {selectedComponent === "domain_geometry" && (
            <>
              <h3>Geometria do Domínio</h3>
              {/* Renderize os dados específicos para Geometria do Domínio */}
            </>
          )}
          {/* Adicione mais condicionais conforme necessário */}
        </div>
      </div>
    </div>
  );
}

export default App;
