import React, { useState } from "react";
import { fetchData } from "./service/BulboSuperficial_backend"; // Importe a função fetchData
import "materialize-css/dist/css/materialize.min.css"; // Importe o arquivo CSS do Materialize

function App() {
  const [soilTexture, setSoilTexture] = useState(null);
  const [hydraulicConductivity, setHydraulicConductivity] = useState(null);
  const [porosity, setPorosity] = useState(null);
  const [fieldCapacity, setFieldCapacity] = useState(null);
  const [wiltingPoint, setWiltingPoint] = useState(null);
  // Adicione mais estados conforme necessário

  const handleSimulation = async () => {
    try {
      const soilProperties = await fetchData("/soil_properties/norte");
      const initialConditions = await fetchData("/initial_conditions/norte");
      const boundaryConditions = await fetchData("/boundary_conditions/norte");
      const climate_parameters = await fetchData("/climate_parameters/norte");
      const domain_geometry = await fetchData("/domain_geometry/norte");
      const time_interval = await fetchData("/time_interval/");
      const saturation_drainage = await fetchData("/saturation_drainage/norte");
      // Adicione mais chamadas de API conforme necessário

      // Extrai os valores individuais e armazena em variáveis
      const {
        texture,
        hydraulic_conductivity,
        porosity,
        field_capacity,
        wilting_point,
      } = soilProperties;
      setSoilTexture(texture);
      setHydraulicConductivity(hydraulic_conductivity);
      setPorosity(porosity);
      setFieldCapacity(field_capacity);
      setWiltingPoint(wilting_point);
      // Atualize outras variáveis conforme necessário
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <div className="container">
      <h1 className="header center-align">Simulação do Bulbo Úmido</h1>
      <div className="row">
        <div className="col s12 center-align">
          <button
            className="btn waves-effect waves-light blue"
            onClick={handleSimulation}
          >
            Simulação
          </button>
        </div>
      </div>

      {soilTexture && (
        <div className="row">
          <div className="col s12">
            <h3>Propriedades do Solo</h3>
            <p>Textura do Solo: {soilTexture}</p>
            <p>Condutividade Hidráulica: {hydraulicConductivity}</p>
            <p>Porosidade: {porosity}</p>
            <p>Capacidade de Campo: {fieldCapacity}</p>
            <p>Ponto de Murcha Permanente: {wiltingPoint}</p>
            {/* Adicione mais parágrafos conforme necessário */}
          </div>
        </div>
      )}

      {/* Renderize outros dados conforme necessário */}
    </div>
  );
}

export default App;
