import React, { useState } from "react";
import { fetchData } from "./service/BulboSuperficial_backend"; // Importe a função fetchData
import "materialize-css/dist/css/materialize.min.css"; // Importe o arquivo CSS do Materialize

function App() {
  const [soilProperties, setSoilProperties] = useState(null);
  const [initialConditions, setInitialConditions] = useState(null);
  const [boundaryConditions, setBoundaryConditions] = useState(null);
  // Adicione mais estados para outras variáveis conforme necessário

  const handleFetch = async (endpoint) => {
    try {
      const responseData = await fetchData(endpoint);
      const { soil_properties, initial_conditions, boundary_conditions } =
        responseData;

      setSoilProperties(soil_properties);
      setInitialConditions(initial_conditions);
      setBoundaryConditions(boundary_conditions);
      // Atualize outros estados conforme necessário
    } catch (error) {
      console.error(error.message);
    }
  };

  return (
    <div className="container">
      <h1 className="header center-align">Simulação do Bulbo Úmido</h1>
      <div className="row">
        <div className="col s4">
          <button
            className="btn waves-effect waves-light blue"
            onClick={() => handleFetch("/soil_properties/norte")}
          >
            Obter Propriedades do Solo
          </button>
        </div>
        <div className="col s4">
          <button
            className="btn waves-effect waves-light blue"
            onClick={() => handleFetch("/initial_conditions/norte")}
          >
            Obter Condições Iniciais
          </button>
        </div>
        <div className="col s4">
          <button
            className="btn waves-effect waves-light blue"
            onClick={() => handleFetch("/boundary_conditions/norte")}
          >
            Obter Condições de Contorno
          </button>
        </div>
      </div>
      {/* Adicione mais botões conforme necessário para outros endpoints */}

      {soilProperties && (
        <div className="row">
          <div className="col s12">
            <h3>Propriedades do Solo</h3>
            <p>Textura do Solo: {soilProperties.texture}</p>
            <p>
              Condutividade Hidráulica: {soilProperties.hydraulic_conductivity}
            </p>
            <p>Porosidade: {soilProperties.porosity}</p>
            {/* Adicione mais parágrafos conforme necessário */}
          </div>
        </div>
      )}

      {/* Renderize outros dados conforme necessário */}
    </div>
  );
}

export default App;
