import React, { useState } from "react";
import { fetchData } from "./service/BulboSuperficial_backend"; // Importe a função fetchData
import "materialize-css/dist/css/materialize.min.css"; // Importe o arquivo CSS do Materialize

function App() {
  const [data, setData] = useState(null);

  const handleFetch = async (endpoint) => {
    try {
      const responseData = await fetchData(endpoint);
      const values = Object.values(responseData);
      setData(values);
    } catch (error) {
      console.error(error.message);
    }
  };

  const renderData = () => {
    if (!data) return null;

    return (
      <div>
        <h3 className="blue-text text-darken-2">Dados Obtidos:</h3>
        <ul className="collection">
          {data.map((value, index) => (
            <li className="collection-item" key={index}>
              {value}
            </li>
          ))}
        </ul>
      </div>
    );
  };

  return (
    <div className="container">
      <h1 className="header center-align">Simulação do Bulbo Úmido</h1>
      <div className="row">
        <div className="col s4">
          <button
            className="btn waves-effect waves-light blue"
            onClick={() => handleFetch("/soil_properties/norte")}
          >
            Obter Propriedades do Solo
          </button>
        </div>
        <div className="col s4">
          <button
            className="btn waves-effect waves-light blue"
            onClick={() => handleFetch("/initial_conditions/norte")}
          >
            Obter Condições Iniciais
          </button>
        </div>
        <div className="col s4">
          <button
            className="btn waves-effect waves-light blue"
            onClick={() => handleFetch("/boundary_conditions/norte")}
          >
            Obter Condições de Contorno
          </button>
        </div>
      </div>
      {/* Adicione mais botões conforme necessário para outros endpoints */}

      <div className="row">
        <div className="col s12">{renderData()}</div>
      </div>
    </div>
  );
}

export default App;
